def get_output_filename(filename, data_source):
    user_id = filename.split('data_')[1].split('_usr')[1].split('.')[0]
    proc_d = PROC_ANDROID_DIR if int(user_id) in ANDROID_PID_LIST else PROC_IOS_DIR
    if int(user_id) in TEST_USERS_LIST:
        return 'test user'
    directory = f"5-{user_id}"
    if not os.path.exists(f'{proc_d}/{directory}'):
        os.makedirs(f'{proc_d}/{directory}')

    return f'{proc_d}/{directory}/{user_id}_{data_source}.csv' if not data_source == 17 else f'{proc_d}/{directory}/{user_id}_1771.csv'
